package com.gaurav.telusko;

public class Ternary {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i = 8; int j = 0;
		
		/*
		 * int i = 8; int j = 0;
		 * 
		 * if(i>6)
		 * 
		 * j = 1;
		 * 
		 * else
		 * 
		 * j = 2;
		 * 
		 * System.out.println(j);
		 */
		
		
		
		j = i>6?1:2;// using ternary operator
		
		System.out.println(j);
		
		
		
		
		
		
		
		

	}

}
